import type { ColumnsType } from 'antd/es/table'
import { Image } from 'antd'
import { Button } from '@mui/material'
import { BASE_URL } from '@/service/config'

import { formatUTC } from '@/utils/format'
import { getOrderStateText } from '@/utils/order.util'
import { useState } from 'react'

export default function useOrderTable() {
  const [operatingOrder, setOperatingOrder] = useState(null)
  const [deliverOpen, setDeliverOpen] = useState(false)

  const columns: ColumnsType<any> = [
    {
      title: '商品图片',
      dataIndex: 'goods',
      align: 'center',
      width: 160,
      render: (goods) => {
        return (
          <Image
            preview={{
              scaleStep: 1
            }}
            width={70}
            height={70}
            src={goods && goods?.imgs?.length ? `${BASE_URL}/goods/${goods.imgs[0].filename}` : ''}
            fallback="data:image/png;base64,"
          />
        )
      }
    },
    {
      title: '用户名',
      dataIndex: 'user',
      align: 'center',
      width: 200,
      render: (value) => value?.name
    },
    {
      title: '商品名称',
      dataIndex: 'goods',
      width: 200,
      align: 'center',
      render: (value) => value?.name
    },
    {
      title: '规格',
      dataIndex: 'goods',
      width: 160,
      align: 'center',
      render: (value) => <span style={{ fontSize: 13, color: '#198bdf' }}>{value?.weight}</span>
    },
    {
      title: '购买数量',
      dataIndex: 'count',
      width: 120,
      align: 'center',
      render: (value) => <span>{value}</span>
    },
    {
      title: '购买单价',
      dataIndex: 'money',
      width: 130,
      align: 'center',
      render: (value) => (
        <span style={{ color: 'red', fontSize: 16 }}>￥{parseFloat(value).toFixed(2)}</span>
      )
    },
    {
      title: '订单状态',
      dataIndex: 'state',
      width: 200,
      align: 'center',
      render: (value) => (
        <span style={{ color: '#198bdf', fontSize: 14, letterSpacing: 2 }}>
          {getOrderStateText(value)}
        </span>
      )
    },
    {
      title: '物流状态',
      dataIndex: 'logistic',
      width: 200,
      align: 'center',
      render: (value) => (
        <span style={{ letterSpacing: 2, color: '#000' }}>{value?.state || '未发货'}</span>
      )
    },
    {
      title: '下单时间',
      dataIndex: 'createAt',
      align: 'center',
      width: 200,
      render: (value) => (
        <span style={{ fontSize: 14, color: '#888', fontWeight: '400' }}>
          {formatUTC(value, 'YYYY-MM-DD HH:mm')}
        </span>
      )
    },
    {
      title: '操作',
      dataIndex: 'operation',
      align: 'center',
      width: 120,
      render: (_, r) => (
        <Button
          disabled={r.logistic}
          onClick={() => {
            setOperatingOrder(r)
            setDeliverOpen(true)
          }}
          style={{ marginRight: 6 }}
        >
          <span style={{ fontSize: 13 }}>去发货</span>
        </Button>
      )
    }
  ]

  return {
    columns,
    operatingOrder,
    deliverOpen,
    setDeliverOpen
  }
}
