import { useCallback, useEffect, useState, memo } from 'react'
import { Table, Input, ConfigProvider, Image } from 'antd'
import { SearchOutlined } from '@ant-design/icons'
import type { ColumnsType } from 'antd/es/table'
import zhCN from 'antd/es/locale/zh_CN'

import { useAppSelector } from '@/store'
import { formatUTC } from '@/utils/format'
import { useAppDispatch } from '@/store/index'
import { loadUsersAction } from '@/store/modules/user'

const User = () => {
  const [name, setName] = useState('')
  const [page, setPage] = useState(1)
  const [size, setSize] = useState(10)
  const dispatch = useAppDispatch()

  const { users, count } = useAppSelector((state) => ({
    users: state.user.users,
    count: state.user.count
  }))

  const loadData = useCallback(() => {
    const params: any = { page, size }
    name && (params.name = name)
    dispatch(loadUsersAction(params))
  }, [name, page, size])

  useEffect(() => {
    loadData()
  }, [name, page, size])

  const columns: ColumnsType<any> = [
    {
      title: '头像',
      dataIndex: 'avatar',
      align: 'center',
      render: (value: string) => {
        return (
          <Image
            width={65}
            height={65}
            preview={false}
            src={'/uploads' + value}
            fallback="data:image/png;base64,"
          />
        )
      }
    },
    {
      title: '用户名',
      dataIndex: 'name',
      align: 'center'
    },
    {
      title: '年龄',
      dataIndex: 'age',
      align: 'center',
      render: (value: number) => {
        return value || '暂无'
      }
    },
    {
      title: '性别',
      dataIndex: 'gender',
      align: 'center',
      render: (value: string) => {
        return value || '其他'
      }
    },
    {
      title: '可用状态',
      dataIndex: 'banned',
      align: 'center',
      render: (value: string) => {
        return value === '1' ? '正常' : '已禁用'
      }
    },
    {
      title: '注册时间',
      dataIndex: 'createdAt',
      align: 'center',
      render: (value) => {
        return formatUTC(value)
      }
    }
  ]

  return (
    <div className="users-info">
      {/* 操作区域 */}
      <div style={{ marginBottom: 10 }}>
        <Input
          style={{ width: 380 }}
          value={name}
          onChange={({ target: { value } }) => setName(value)}
          prefix={<SearchOutlined />}
          placeholder="请输入用户名"
        />
      </div>
      <ConfigProvider locale={zhCN}>
        <Table
          columns={columns}
          rowKey={(record: any) => record.name}
          locale={{ emptyText: '暂无数据' }}
          dataSource={users}
          pagination={{
            total: count,
            showSizeChanger: true,
            onChange: (page: number) => setPage(page),
            onShowSizeChange: (_, pageSize) => setSize(pageSize)
          }}
        />
      </ConfigProvider>
    </div>
  )
}

export default memo(User)
