import { Image, Popconfirm } from 'antd'
import { Button } from '@mui/material'
import type { ColumnsType } from 'antd/es/table'
import { bannedUserReq } from '@/service/modules/user'
import { changeOpen } from '@/store/modules/main'
import { formatUTC } from '@/utils/format'
import { useAppDispatch } from '@/store'
import { BASE_URL } from '@/service/config'

interface IProps {
  loadData: Function
}

export function useUserTable({ loadData }: IProps) {
  const dispatch = useAppDispatch()

  // 禁用与解封
  const bannedClick = (id: number, banned: string) => {
    bannedUserReq(id, banned).then((res) => {
      if (res.message === 'ok') {
        dispatch(changeOpen({ message: '操作成功~', type: 'success' }))
        loadData()
      }
    })
  }
  const columns: ColumnsType<Record<string, any>> = [
    {
      title: '头像',
      dataIndex: 'avatar',
      width: 150,
      align: 'center',
      render: (avatar) => {
        return (
          <Image
            width={65}
            height={65}
            preview={false}
            src={avatar ? `${BASE_URL}/avatar/${avatar.filename}` : ''}
            fallback="data:image/png;base64,"
          />
        )
      }
    },
    {
      title: '用户名',
      width: 180,
      dataIndex: 'name',
      align: 'center'
    },
    {
      title: '手机号',
      width: 200,
      dataIndex: 'phone',
      align: 'center',
      render: (value: string) => {
        return value || '无'
      }
    },
    {
      title: '年龄',
      dataIndex: 'age',
      align: 'center',
      width: 120,
      render: (value: number) => {
        return value || '暂无'
      }
    },
    {
      title: '性别',
      dataIndex: 'gender',
      align: 'center',
      width: 120,
      render: (value: string) => {
        return value === '0' ? '女' : value === '1' ? '男' : '其他'
      }
    },
    {
      title: '可用状态',
      dataIndex: 'banned',
      align: 'center',
      width: 160,
      render: (value: string) => {
        return value === '0' ? (
          <span style={{ color: 'green' }}>正常</span>
        ) : (
          <span style={{ color: '#0288d1' }}>已禁用</span>
        )
      }
    },
    {
      title: '注册时间',
      dataIndex: 'createdAt',
      width: 170,
      align: 'center',
      render: (value) => {
        return formatUTC(value, 'YYYY-MM-DD HH:mm')
      }
    },
    {
      title: '操作',
      dataIndex: 'operation',
      align: 'center',
      width: 150,
      render: (_, { id, banned }: any) => {
        return (
          <div>
            <Popconfirm
              title={`你确认要${banned === '0' ? '禁用该用户' : '解封该用户'}吗？`}
              onConfirm={() => bannedClick(Number(id), banned)}
              okText="确定"
              cancelText="取消"
              placement="topRight"
            >
              <Button color={banned === '0' ? 'error' : 'info'} size="small">
                {banned === '0' ? '禁用' : '解封'}
              </Button>
            </Popconfirm>
          </div>
        )
      }
    }
  ]

  return {
    columns
  }
}
